<?php

$myList=["Türkei","Italien","Griechenland","Spanien", "Kroatien"];
$myList1=["<img src='data:image/png;base64,' alt='Türkei Flag' title='Türkei' >",
    "<img src='data:image/png;base64,' alt='Italien Flag' title='Italien' >",
    "<img src='data:image/png;base64,' alt='Griechenland Flag' title='Griechenland' >",
    "<img src='data:image/png;base64,' alt='Spanien Flag' title='Spanien' >",
    "<img src='data:image/png;base64,' alt='Kroatiem Flag' title='Kroatien' >"
];
$a=0;
echo"<ol type='1'>";
foreach ($myList as $my){
    echo"<li>$my&nbsp;$myList1[$a]</li>";
    $a++;
}
echo"</ol>";
